/// \file
/// This file declares the targeting of the RegisterBankInfo class for WDC65816.
/// \todo This should be generated by TableGen.
//===----------------------------------------------------------------------===//

#ifndef LLVM_LIB_TARGET_WDC65816_GLSEL_WDC65816REGISTERBANKINFO_H
#define LLVM_LIB_TARGET_WDC65816_GLSEL_WDC65816REGISTERBANKINFO_H

#include "llvm/CodeGen/RegisterBankInfo.h"

#define GET_REGBANK_DECLARATIONS
#include "WDC65816GenRegisterBank.inc"
#undef GET_REGBANK_DECLARATIONS

namespace llvm {

class TargetRegisterInfo;

class WDC65816GenRegisterBankInfo : public RegisterBankInfo {
protected:
#define GET_TARGET_REGBANK_CLASS
#include "WDC65816GenRegisterBank.inc"
#undef GET_TARGET_REGBANK_CLASS
};

/// This class provides the information for the target register banks.
class WDC65816RegisterBankInfo final : public WDC65816GenRegisterBankInfo {
public:
  WDC65816RegisterBankInfo(const TargetRegisterInfo &TRI);

  const RegisterBank &getRegBankFromRegClass(const TargetRegisterClass &RC,
                                             LLT) const override;

  const InstructionMapping &
  getInstrMapping(const MachineInstr &MI) const override;
};
} // end namespace llvm
#endif // LLVM_LIB_TARGET_WDC65816_GLSEL_WDC65816REGISTERBANKINFO_H
